/*
We're constantly improving the code you see. 
Please share your feedback here: https://form.asana.com/?k=uvp-HPgd3_hyoXRBw1IcNg&d=1152665201300829
*/

import PropTypes from "prop-types";
import React from "react";
import { useReducer } from "react";
import { Icon12 } from "../../icons/Icon12";
import { Search } from "../../icons/Search";
import { IconButton } from "../IconButton";
import "./style.css";

export const SearchBar = ({
  show1StTrailingIcon = true,
  placeholderText = "Hinted search text",
  show2NdTrailingIcon = false,
  stateProp,
  showAvatar,
  className,
  iconButtonIcon = <Icon12 className="icon-2" color="#49454F" />,
  iconButtonStyleOutlinedStateClassName,
  trailingElementsClassName,
  override = <Search className="icon-2" color="#49454F" />,
}) => {
  const [state, dispatch] = useReducer(reducer, {
    state: stateProp || "enabled",
    showAvatar: showAvatar || false,
  });

  return (
    <div className={`search-bar ${className}`}>
      <div className={`div state-0-${state.state}`}>
        {state.state === "pressed" && (
          <>
            <img className="ripple" alt="Ripple" src="/img/ripple-1.svg" />
            <IconButton icon={<Icon12 className="icon-2" color="#49454F" />} stateProp="enabled" style="standard" />
            <div className="content">
              <div className="supporting-text">{placeholderText}</div>
            </div>
            <div className="trailing-elements">
              {show1StTrailingIcon && (
                <IconButton icon={<Search className="icon-2" color="#49454F" />} stateProp="enabled" style="standard" />
              )}

              {state.showAvatar && (
                <div className="avatar-target">
                  <div className="avatar">
                    <div className="initial">A</div>
                  </div>
                </div>
              )}
            </div>
          </>
        )}

        {["enabled", "hovered"].includes(state.state) && (
          <>
            <IconButton
              className={iconButtonStyleOutlinedStateClassName}
              icon={iconButtonIcon}
              stateProp="enabled"
              style="standard"
            />
            <div
              className="content"
              onMouseEnter={() => {
                dispatch("mouse_enter");
              }}
              onMouseLeave={() => {
                dispatch("mouse_leave");
              }}
            >
              <div className="supporting-text">{placeholderText}</div>
            </div>
            <div className={`trailing-elements ${trailingElementsClassName}`}>
              {show1StTrailingIcon && <IconButton icon={override} stateProp="enabled" style="standard" />}

              {state.showAvatar && (
                <div className="avatar-target">
                  <div className="avatar">
                    <div className="initial">A</div>
                  </div>
                </div>
              )}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

function reducer(state, action) {
  switch (action) {
    case "mouse_enter":
      return {
        ...state,
        state: "hovered",
      };

    case "mouse_leave":
      return {
        ...state,
        state: "enabled",
      };
  }

  return state;
}

SearchBar.propTypes = {
  show1StTrailingIcon: PropTypes.bool,
  placeholderText: PropTypes.string,
  show2NdTrailingIcon: PropTypes.bool,
  stateProp: PropTypes.oneOf(["hovered", "pressed", "enabled"]),
  showAvatar: PropTypes.bool,
};
